mapper.update:
UPDATE dish


        AND d.name LIKE concat('%', #{name}, '%')


WHERE id = #{id}

mapper.select:
SELECT d.*, c.name AS categoryName FROM dish d LEFT JOIN category c ON d.catory_id = c.id
AND d.name LIKE concat('%', #{name}, '%')
AND d.categoryId = #{categoryId}
AND d.status = #{status}
ORDER BY d.create_time DESC
